(function() {
  function buildQuiz() {
    // we'll need a place to store the HTML output
    const output = [];

    // for each question...
    myQuestions.forEach((currentQuestion, questionNumber) => {
      // we'll want to store the list of answer choices
      const answers = [];

      var imageArray = [
        ["http://susanevans.org/blog/wp-content/uploads/2010/10/DSC_1246.jpg", "https://media.mnn.com/assets/images/2015/10/kids-recess-running.jpg.653x0_q80_crop-smart.jpg",
          "https://parentinghealthybabies.com/wp-content/uploads/2014/02/Hide-and-Seek.jpg", "https://upload.wikimedia.org/wikipedia/commons/4/47/Little_girl_on_swing.jpg"],
        ["http://insulinnation.com/wp-content/uploads/2014/11/shutterstock_182831945_fearless_child_620px.jpg", "https://www.oxfordlearning.com/wp-content/uploads/2015/03/how-to-motivate-kids-in-school-860x420.jpg",
          "https://i1.wp.com/embracingthespectrum.com/wp-content/uploads/2017/04/raising-independent-children-montessori-approach.png?w=589", "https://www.stayathomemum.com.au/wp-content/uploads/2016/02/Simple-Relaxation-Activities-For-Children.jpg"],
        ["https://www.unilad.co.uk/wp-content/uploads/2017/11/T-Rex-Jurassic-World-Universal-Pictures.jpg", "http://a57.foxnews.com/images.foxnews.com/content/fox-news/travel/2018/03/29/man-on-safari-captures-terrifying-moment-when-cheetah-jumps-into-his-open-vehicle/_jcr_content/par/featured_image/media-0.img.jpg/931/524/1522364799015.jpg?ve=1&tl=1&text=big-top-image",
          "http://cdn0.wideopenpets.com/wp-content/uploads/2016/01/rsz_chameleon-03-770x405.jpg", "https://defenders.org/sites/default/files/styles/homepage-feature-2015/public/bald-eagle_stephen-oachs.png?itok=TbZnxOLy"],
        ["https://www.5minutesformom.com/wp-content/uploads/2013/11/student_doing_a_math_problem.jpg", "http://i.huffpost.com/gen/1987511/images/n-86803743-628x314.jpg",
          "https://www.teachersoncall.ca/wp-content/uploads/educatingkidsthroughscience.jpg", "https://i.ytimg.com/vi/bSB8zC4LI_k/maxresdefault.jpg"],
        ["https://media-cdn.tripadvisor.com/media/photo-s/01/20/7a/82/new-beach-hut-at-the.jpg", `data:image/jpeg;base64,`,
          "http://www.mykidsadventures.com/wp-content/uploads/2014/07/jdj-get-your-kids-involved.png", "https://i.ytimg.com/vi/4PBOv5m4V48/maxresdefault.jpg"],
        ["https://www.gmc.com/content/dam/gmc/na/us/english/index/gmc-life/shared-assets/gmc-life-related-article-AT4-reveal.jpg?imwidth=600", "https://cdn.dealerspike.com/imglib/v1/800x600/imglib/trimsdb/4904041-0-18576691.jpg",
          "https://keyassets.timeincuk.net/inspirewp/live/wp-content/uploads/sites/11/2017/06/Genius-900-Tuned_SCOTT-Sports_bike_Close-Up_2018_22-e1517576991890.jpg", "https://www.experiencedays.co.uk/images/helicopter-ride-in-cardiff-1920x1080-resize.JPG"],
        ["https://i.ytimg.com/vi/FJjg4ZOZgHY/maxresdefault.jpg", "https://d2v9y0dukr6mq2.cloudfront.net/video/thumbnail/kids-riding-front-of-rollercoaster_-jhlyf-bh__F0000.png",
          "https://c1.staticflickr.com/8/7755/18095621009_9a83b06440_b.jpg", "https://amusementrides.org/wp-content/uploads/2016/04/quality-swing-ride-for-sale-in-China.jpg"],
        ["https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQa9xWEPmpQFW_2DadNiAh9oeUif8IoFtIPPZ2EV4z81L8oMCTE", `data:image/jpeg;base64,`,
          `data:image/jpeg;base64,`, `https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTZLr2jg5tFikC_3GNUvoyV7BU0kua-XFv4SrNyWEAQ5nYC0nA`]
      ];

      var imageIndex = 0;

      // and for each available answer...
      for (letter in currentQuestion.answers) {
        // ...add an HTML radio button
        answers.push(
          `<label>
            <input type="image" alt="${letter}" src=${imageArray[questionNumber][imageIndex]} width="240" height="160">
            <input type="radio" name="question${questionNumber}" value="${letter}">
            ${currentQuestion.answers[letter]}
          </label>`
        );
        imageIndex++;
      }

      // add this question and its answers to the output
      output.push(
        `<div class="question"> ${currentQuestion.question} </div>
        <div class="answers"> ${answers.join("")} </div>`
      );
    });

    // finally combine our output list into one string of HTML and put it on the page
    quizContainer.innerHTML = output.join("");
  }

  function showResults() {
    // gather answer containers from our quiz
    const answerContainers = quizContainer.querySelectorAll(".answers");

    // keep track of user's answers in an array (each index holds the number of times you click on a through d)
    var powers = [];
    for(var i=0; i<4; i++) {
      powers[i] = 0;
    }
    var max = 0;
    var max_index = 0;
    var power;

    // for each question...
    myQuestions.forEach((currentQuestion, questionNumber) => {
      // find selected answer
      const answerContainer = answerContainers[questionNumber];
      const selector = `input[name=question${questionNumber}]:checked`;
      const userAnswer = (answerContainer.querySelector(selector) || {}).value;

      // if answer is correct
      if (userAnswer === "a") {
        // add to strength
        powers[0]++;
      } else if (userAnswer === "b") {
        // add to strength
        powers[1]++;
      } else if (userAnswer === "c") {
        // add to invisibility
        powers[2]++;
      } else if (userAnswer === "d") {
        // add to flight
        powers[3]++;
      }
    });

    // find the most clicked on answer, and store it in a variable called max
    for(var j=0; j<4; j++) {
      if(powers[j] > max) {
        max = powers[j];
        max_index = j;
      }
    }

    // set your super power according to the answer you clicked on most
    if(max_index === 0) {
      power = "super strength";
    }
    if(max_index === 1) {
      power = "super speed";
    }
    if(max_index === 2) {
      power = "invisibility";
    }
    if(max_index === 3) {
      power = "flying";
    }

    // show your super power
    resultsContainer.innerHTML = `Your super power is ${power}`;
  }

  //select our HTML tags and store references to these elements in variables
  const quizContainer = document.getElementById("quiz");
  const resultsContainer = document.getElementById("results");
  const submitButton = document.getElementById("submit");
  const myQuestions = [
    {
      question: "What do you like to do when you're outisde?",
      answers: {
        a: "Climb a tree",
        b: "Play tag",
        c: "Play hide-and-seek",
        d: "Swing on the swings"
      },
    },
    {
      question: "What word best describes you?",
      answers: {
        a: "Fearless",
        b: "Motivated",
        c: "Independent",
        d: "Easygoing"
      },
    },
    {
      question: "Which animal would you most likely be?",
      answers: {
        a: "T-Rex",
        b: "Cheetah",
        c: "Chameleon",
        d: "Bald Eagle"
      },
    },
    {
      question: "What's your favorite subject?",
      answers: {
        a: "Math",
        b: "Writing",
        c: "Science",
        d: "Geography"
      },
    },
    {
      question: "You're stranded on an island. How do you pass the time?",
      answers: {
        a: "Build a beach hut.",
        b: "Run along the beach.",
        c: "Make sand castles.",
        d: "Climb up the highest tree."
      },
    },
    {
      question: "What's your ideal mode of transportation?",
      answers: {
        a: "truck",
        b: "motorcycle",
        c: "bike",
        d: "helicopter"
      },
    },
    {
      question: "What's the coolest thing at a carnival?",
      answers: {
        a: "hammer game",
        b: "roller coaster",
        c: "fun house",
        d: "swing ride"
      },
    },
    {
      question: "Choose a breakfast.",
      answers: {
        a: "bacon and eggs",
        b: "granola bar",
        c: "pancakes",
        d: "cereal"
      },
    }
  ];

  // display quiz right away
  buildQuiz();

  // on submit, show results
  submitButton.addEventListener("click", showResults);
})();
